mapper.select:
select ag_machines_new.id, ag_machines_new.val_key, ag_machines_new.machine_code, ag_keys.qq, ag_keys.expiration_time, ag_keys.validate_type, ag_keys.used, ag_keys.key_type, ag_keys.last_val_time, ag_keys.external_card from ag_keys join ag_machines_new on ag_keys.val_key = ag_machines_new.val_key where (key_type = 5 or ag_keys.expiration_time is null or ag_keys.expiration_time > now()) and ag_keys.validate_type = #{validateType} and
ag_keys.qq = #{condition}
ag_machines_new.machine_code = #{condition}
